static void f(String t){int[][]w=new int[][]{{1,2,3},{4,5,6},{7,8,9},{1,4,7},{2,5,8},{3,6,9},{1,5,9},{3,5,7}};char[]m=new char[10];Arrays.fill(m,'-');char[]a=t.toCharArray();for(int x=0;x<a.length;x++){int pos=Integer.parseInt(a[x]+"");if(x%2==0)m[pos]='O';else m[pos]='X';for(int z=0;z<w.length;z++){char i=m[w[z][0]];char j=m[w[z][1]];char k=m[w[z][2]];if(i==j&&j==k&&i!='-'){System.out.println(i);return;}}}System.out.println("?");}