static void f(String s){byte[]bs=null;try{bs=s.getBytes("ASCII");}catch(Exception e){}Arrays.sort(bs);Map<Byte,Integer>cs=new LinkedHashMap<>();for(byte b:bs){Integer ct=cs.get(b);if(ct==null){ct=0;}cs.put(b,++ct);}Map<Byte,Integer>r=new LinkedHashMap<>();Stream<Map.Entry<Byte,Integer>> st=cs.entrySet().stream();st.sorted(Comparator.comparing(e->e.getValue())).forEachOrdered(e->r.put(e.getKey(),e.getValue()));String s2="";for(Map.Entry<Byte,Integer>e:r.entrySet()){for(int i=0;i<e.getValue();i++){s2+=(char)(e.getKey().byteValue());}}System.out.print(s2);}