static void f(String s){Map<Integer,List<String>>m=new TreeMap<>();char[]a=s.toCharArray();Arrays.sort(a);String r=new String(a);int b=0;for(int e=1;e<=r.length();e++){if(e<r.length()&&r.charAt(b)==r.charAt(e))continue;int i=e-b;if(m.containsKey(i)){List<String>list=m.get(i);list.add(r.substring(b,e));m.put(i,list);}else{List<String>l=new ArrayList<>();l.add(r.substring(b,e));m.put(i,l);}b=e;}for(List<String>d:m.values())d.stream().forEach(System.out::print);}