public static void f(String i) {Map<Character,Integer> map = new HashMap<>();for(int j = 0;j< i.length(); j++) {Integer c = map.get(i.charAt(j));map.put(i.charAt(j),c == null ? 1 : c + 1);}map.entrySet().stream().sorted((o1,o2) -> {int c = o1.getValue().compareTo(o2.getValue());if (c == 0) {c = o1.getKey().compareTo(o2.getKey());}return c;}).forEach(characterIntegerEntry -> {for (int i1 = 0; i1 < characterIntegerEntry.getValue(); i1++)System.out.print(characterIntegerEntry.getKey());});}