static void f(String s){do{s=r(s);}while(s.contains("|\\"));if(s.substring(0,1).equals("\\"))s="__"+s.substring(1);System.out.println(s);}static String r(String s){String begin="";if(s.length()==1&&s.equals("/")){return "__";}if(s.length()==0||s.length()==1&&s.equals("|"))return s;if(s.substring(0,1).equals("/")){if(s.substring(1,2).equals(" ")){begin+= "__";s=s.substring(2);}else if(s.substring(1,2).equals("|")){if(s.length()>=3&&!s.substring(2,3).equals("\\")){begin+="/";s="/"+s.substring(2);}else{begin+="/";s="/"+s.substring(2);}}else{begin+="/";s=s.substring(1);}}else if(s.substring(0,1).equals("|")){if(s.substring(1,2).equals("\\")){begin+="\\\\";s=s.substring(2);}else{begin+=s.substring(0,1);s=s.substring(1);}}else if(s.substring(0,1).equals(" ")){if(s.substring(1,2).equals("\\")){begin+="__";s=s.substring(2);}else{begin+=s.substring(0,1);s=s.substring(1);}}else{begin+=s.substring(0,1);s=s.substring(1);}return begin+r(s);}
