static void f(String aa){char[]a=aa.toCharArray();final Map<Character,Integer>m=new HashMap<>();for(Character c:a){if(m.get(c)!=null){m.put(c,m.get(c)+1);}else{m.put(c,1);}}TreeMap<Character,Integer>srtd=new TreeMap(new Comparator<Character>(){@Override public int compare(Character o1,Character o2){return m.get(o1)>m.get(o2)?1:(m.get(o1)==m.get(o2)?(o1>o2?1:-1):-1);}});srtd.putAll(m);for(Map.Entry<Character,Integer>en:srtd.entrySet()){for(int i=0;i<en.getValue();i++)System.out.print(en.getKey());}}