static void f(String g){int[]b=new int[9];for(int i=0;i<g.length();i++){int sN=Integer.parseInt(String.valueOf(g.charAt(i)))-1;if(i%2==0){b[sN]=1;if(cb(b,1)){System.out.println("O");return;}}else{b[sN]=2;if(cb(b,2)){System.out.println("X");return;}}}System.out.println("?");}static boolean cb(int[] b,int p){if((b[2]==p&&b[5]==p&&b[8]==p)||(b[1]==p&&b[4]==p&&b[7]==p)||(b[0]==p&&b[3]==p&&b[6]==p)||(b[0]==p&&b[1]==p&&b[2]==p)||(b[3]==p&&b[4]==p&&b[5]==p)||(b[6]==p&&b[7]==p&&b[8]==p)||(b[0]==p&&b[4]==p&&b[8]==p)||(b[2]==p&&b[4]==p&&b[6]==p)){return true;}return false;}
