static char[][]m;static char t='a';static int x,y;static void f(String s){m=new char[s.split("\n").length][];for(int i=0;i<m.length;i++){m[i]=s.split("\n")[i].toCharArray();}for(int i=0;i<m.length;i++){for(int j=0;j<m[i].length;j++){x=i;y=j;switch(m[i][j]){case'>':l(0,1);t++;break;case'v':l(1,0);t++;break;case'^':l(-1,0);t++;break;case'<':l(0,-1);t++;break;}}}boolean f=false;for(int i=0;i<m.length;i++){for(int j=0;j<m[i].length;j++){if(m[i][j]=='x'){System.out.println(":/");return;}if(m[i][j]=='@'){f=true;}}}if(f){System.out.println(":(");return;}System.out.println(":D");}static void l(int a,int b){x+=a;y+=b;if(x<0||y<0||x>=m.length||y>=m[0].length){return;}try{Integer.parseInt(String.valueOf(m[x][y]));char portal=m[x][y];m[x][y]='P';for(int i=0;i<m.length;i++){for(int j=0;j<m[i].length;j++){if(m[i][j]==portal){m[x][y]=m[i][j];x=i;y=j;l(a,b);return;}}}}catch(Exception e){};if(m[x][y]=='@'){m[x][y]='*';}else if(m[x][y]!=t&&m[x][y]>='a'&&m[x][y]<'x'){m[x][y]='x';}else if(m[x][y]==' '){m[x][y]=t;l(a,b);}else if(m[x][y]==t){l(a,b);}else if(m[x][y]=='/'){l(-b,-a);}else if(m[x][y]=='\\'){l(b,a);}}