static void f(String s,int n,int w){String ss[]=s.split("(?<=\\G.{"+(w)+"})"),r[],spaces="";int rows=(int)ss.length/n,i;if(rows*n<ss.length)++rows;int sum=n*rows;for(i=0;i<ss.length;++i){while(ss[i].length()<w)ss[i]=ss[i]+" ";}for(i=0;i<w;++i){spaces+=" ";}r=new String[rows];for(i=0;i<rows;++i){r[i]="";}for(i=0;i<ss.length;++i){r[i%rows]=r[i%rows]+(r[i%rows].isEmpty()?"":"|")+ss[i];}for(i=ss.length;i<sum;++i){r[i%rows]=r[i%rows]+(r[i%rows].isEmpty()?"":"|")+spaces;}for(i=0;i<r.length;++i)System.out.print(r[i]+(i<r.length-1?"\n":""));}