static void f(String s){String k="",r=s;if(r.startsWith("|\\")|r.startsWith("\\"))r="__"+r.substring(1);while(k!=r){k=r;r=r.replace("/ ","__").replace("/| ","/__").replace(" \\","__").replace("/||\\","//\\\\").replace(" |\\","__\\").replace("\\|\\","\\\\\\").replace("||\\","|\\\\").replace("/|/","///").replace("/||","//|");}if(r.endsWith("/|")|r.endsWith("/"))r=r.substring(0,r.length()-1)+"__";System.out.print(r);}