static void f(String s){String[] r=s.split("\n");int n=r.length,m=r[0].length();char[][]M=new char[n][m];for(int i=0;i<n;i++){for(int j=0;j<m;j++) {M[i][j]=r[i].charAt(j);}}boolean C=1==2;for(int i=0;i<n;i++) {for (int j=0;j<m;j++) {int x=i,y=j;int d=-1;char c=M[i][j];if(c=='>')d=0;if(c=='<')d=2;if(c=='v')d=3;if(c=='^')d=1;if(d>-1){while(true){x+=d==3?1:(d==1?-1:0);y+=d==0?1:(d==2?-1:0);if(x<0||x>=n||y<0||y>=m){break;}c = M[x][y];if(c=='@'||c=='!'){M[x][y]='!';break;}if((c=='>'&&d==2)||(c=='<'&&d==0)||(c=='^'&&d==3)||(c=='v'&&d==1)){C=true;}if(c==' '){M[x][y]='.';}if(c==':'){C=true;}if("#><^v".contains(c+"")) {break;}if(c=='/')d=d^1;if(c=='\\')d=3-d;if(c >='0'&&c<='9'){boolean F=1==2;for(int k=0;k<n;k++){for(int l=0;l<m;l++){if(M[k][l]==c&&(k!=x||l!=y)&&!F){x=k;y=l;F = true;}}}}}}for(int k=0;k<n;k++){for(int l=0;l<m;l++){if(M[k][l]=='.')M[k][l]=':';}}}}boolean a=true;for(int i=0;i<n;i++){for (int j = 0; j < m; j++){if(M[i][j]=='@')a=false;}}if(C){System.out.print(":/");}else if(a){System.out.print(":D");}else {System.out.print(":(");}}
