static void f(int n) {var r=new[]{"m","cm","d","cd","c","xc","l","xl","x","Mx","v","Mv","M","CM","D","CD","C","XC","L","XL","X","IX","V","IV","I"};var x=new[]{1000000,900000,500000,400000,100000,90000,50000,40000,10000,9000,5000,4000,1000,900,500,400,100,90,50,40,10,9,5,4,1};var c="";var c1="";for(var i=0;i<r.Length;i++){while(n>=x[i]){c+=r[i].ToUpper();c1+=r[i][0]>'X'?'_':' ';c1+=r[i].Length>1&&r[i][1]>'X'?"_":"";n-=x[i];}}Console.Write(c1 + "\n" + c);}