static String X="";static void f(String i){m(" "+i+" ");System.out.println(X.substring(1,X.length()-1));}static void m(String t){StringBuilder r=new StringBuilder();boolean q=false;for(int i=1;i<t.length()-1;i++){String w=String.valueOf(t.charAt(i));String s=String.valueOf(t.charAt(i-1));String f=String.valueOf(t.charAt(i+1));if("|".equals(w)){if("/".equals(s)&&!"\\".equals(f))r.append("/");else if("\\".equals(f)&&!"/".equals(s))r.append("\\");else r.append(w);}else if("/".equals(w)&&" ".equals(f)){r.append("_");r.append("_");q=true;}else if("\\".equals(w)&&" ".equals(s)&&(i<2||(i>1&&!"/".equals(String.valueOf(t.charAt(i-2)))))){if(r.length()>0)r.deleteCharAt(r.length()-1);r.append("_");r.append("_");}else if(" ".equals(w)&&q){q=false;}else{r.append(w);}}if(r.toString().trim().equals(t.trim()))X=t;else m(" "+r.toString()+" ");}
