static void f(String s){if (h(s,0,1,2,3)||h(s,0,1,3,4)||h(s,0,2,3,4)|| h(s,1,2,3,4)||h(s,0,1,4,4)||h(s,0,2,4,5)|| h(s,0,3,4,5)||h(s,1,2,4,5)||h(s,1,3,4,5)|| h(s,2,3,4,5))return;System.out.println("?");}static boolean h(String s,int i,int j,int k,int l){String o=s.replaceAll("(.).","$1"),x=s.replaceAll(".(.)","$1");if(s.length()%2==1)x=x.substring(0,x.length()-1);if(o.length()>=l&&g(o,i,j,k)%3==0){System.out.println("O");return true;}else if(x.length()>=l&&g(x,i,j,k)%3==0){System.out.println("X");return true;}return false;}static int g(String s,int i,int j,int k) {return s.charAt(i)+s.charAt(j)+s.charAt(k)-3*'0';}