static void f(int cost){ int tip = 0; int rounding; if(cost < 2000){ rounding=cost%100; tip= rounding != 0 ? 100-rounding : 0; } else if (cost < 5000) { rounding = cost % 500; tip = rounding != 0 ? 500-rounding : 0; } else { tip = (int )(0.1 * cost); rounding = ((cost+tip) % 500); tip = rounding < 250 ? tip-rounding : tip+(500-rounding); } if (tip == 0 || tip > 1500){ System.out.println("PAID BY CARD"); } else if (tip % 100 > 0){ System.out.println("KEEP THE CHANGE YOU FILTY ANIMAL"); } else System.out.println("HERE IS " + (cost+tip)); }