static void f(String input){LinkedHashMap<String,int[]>h=new LinkedHashMap();h.put("1234567890-=~!@#$%^&*()_+", new int[30]);h.put("qwertyuiop[]QWERTYUIOP{}",new int[30]);h.put("asdfghjkl;'\\ASDFGHJKL:\"|",new int[30]);h.put("zxcvbnm,./ZXCVBNM<>?",new int[30]);int[]r=new int[4];int a=0;for(Map.Entry<String,int[]>e:h.entrySet()){String k=e.getKey();int[]v=e.getValue();for(char c:input.toCharArray()){int n=k.indexOf(c);if(n>=0){v[n]++;}}for(int i=0;i<v.length;i++){r[a]+=v[i];}a++;}List<String>l=new ArrayList<>(h.keySet());List<int[]>l1=new ArrayList<>(h.values());a=Integer.MAX_VALUE;int m=0;for(int i=0;i<r.length;i++){if(r[i]!=0&&r[i]<a){a=r[i];m=i;}else if(r[i]==a){int o=g(l1.get(i));int p=g(l1.get(m));if(o<p){m=o;}}}for(int i=0;i<l.get(m).length();i++){if(l1.get(m)[i]>0){System.out.print(l.get(m).charAt(i));}}}
static int g(int[]a){int s=0;for(int i=0;i<a.length;i++){if(a[i]>0){s+=1;}}return s;}