static void f(String q){Map<Integer,Integer>m=new HashMap<>();Map<Integer,List<Integer>>n=new HashMap<>();List<Integer>list=q.chars().mapToObj(c->c).collect(Collectors.toList());list.forEach(i->m.put(i,m.get(i)==null?1:m.get(i)+1));m.forEach((k,v)->n.put(v,new ArrayList()));m.forEach((k,v)->n.get(v).add(k));n.forEach((k,v)->Collections.sort(v));List<Integer>s=new ArrayList();s.addAll(n.keySet());Collections.sort(s);for(Integer i:s){for(int y:n.get(i)){for(int a=0;a<i;a++){char c=(char)y;System.out.print(c);}}}}