public static void f(String s){s.chars().mapToObj(i->(char)i).collect(Collectors.groupingBy(c->c,Collectors.reducing(0,c->1,Integer::sum))).entrySet().stream().sorted((c1,c2)->c1.getValue().compareTo(c2.getValue())!=0?c1.getValue().compareTo(c2.getValue()):c1.getKey().compareTo(c2.getKey())).forEach(e->IntStream.rangeClosed(0,e.getValue()).forEach(g->System.out.print(e.getKey())));}