public static void f(String s){Map<Character,Long>m=new TreeMap<>();int l=s.length();for(int i=0;i<l;i++){Character c=s.charAt(i);if(m.containsKey(c)){m.put(c,m.get(c)+1);}else{m.put(c,1l);}}
String r="";for(long j=0;j<l;j++){long h=j;r+=m.entrySet().stream().filter(e->e.getValue()==h).map(e->new String(new char[e.getValue().intValue()]).replace("\0",e.getKey().toString())).collect(Collectors.joining(""));}
System.out.print(r);}