static void f(String s, int n, int w){int x=0,y=0,q;if((s.length()%(w*n)==0)){q=s.length()/(w*n);}else{q=s.length()/(w*n)+1;}String[][]r=new String[n][q];for(int i=0;i<s.length();){if(i+w>s.length()){r[x][y]=s.substring(i,s.length());}else{r[x][y]=s.substring(i,i+w);}if(y==q-1){y=0;x++;}else{y++;}i+=w;}for(int i=0;i<q;i++){for(int j=0;j<n;j++){if(r[j][i] == null){r[j][i] = " ";}if(r[j][i].length()<w){String sw="                    ";r[j][i]+=sw.substring(20-(w-r[j][i].length()));}if(j+1<n){System.out.print(r[j][i].concat("|"));}else{System.out.println(r[j][i]);j = 0;break;}}}}